import { View, Text, StyleSheet, Image,TouchableOpacity, Linking } from 'react-native'
import React, {useState, useEffect, useContext} from 'react'
import { useNavigation } from '@react-navigation/native'
import AntDesignIcon from 'react-native-vector-icons/AntDesign'
import { WhatsAppOutlined } from '@ant-design/icons'
import { DataProcessorContext } from '../context/DataProcessor'

const RoommateDetails = () => {

// page navagation
const navigation = useNavigation();

const {
    selectedRoommate
  } = useContext(DataProcessorContext);

  return (
    <View>
        {selectedRoommate &&
            <Image resizeMode='cover' style={{aspectRatio: 2, width: '100%', borderWidth: '3.5px', marginTop: '10px' }} source={selectedRoommate.profilePic}></Image>
        }
    </View>
  )
}


const styles = StyleSheet.create({

  container: {
    flex: 1,
    display: "flex",
    alignItems: 'center',
  },


})

export default RoommateDetails
