import { SafeAreaView,StyleSheet, ScrollView, View, Text, Dimensions, Image } from 'react-native'
import React from 'react'
import react, {useState} from 'react'

const images = [
  'https://cdn-prod.medicalnewstoday.com/content/images/articles/321/321450/stressed-woman-at-work.jpg',
  'https://health.clevelandclinic.org/wp-content/uploads/sites/3/2022/05/10WaysToEaseStress-1166219231-770x533-1.jpg',
  'https://www.popsci.com/uploads/2021/10/05/AUA_stress.jpg',
]

const WIDTH = Dimensions.get('window').width
const HEIGHT = Dimensions.get('window').height

const RoomViewPostScreen = () => {
  const [imageActive, setimageActive] = useState(0)

  onchange = (nativeEvent) => {
    if (nativeEvent) {
      const slide = Math.ceil(nativeEvent.contentOffset.x / nativeEvent.layoutMeasurement.width)
      if (slide != imageActive) {setimageActive(slide)}
    }
  }

  return (
      <View style= {styles.displayContainer}>
          <SafeAreaView style= {styles.container}>
              <View style= {styles.wrap}>
              <ScrollView
                onScroll={({nativeEvent}) => onchange(nativeEvent)}
                showsHorizontalScrollIndicator={false}
                pagingEnabled
                horizontal
                style={styles.wrap}
                >

                {
                  images.map((e, index) => 
                  <Image 
                    key={e}
                    resizeMode='stretch'
                    style={styles.wrap}
                    source={{uri: e}}
                    />
                  )
                }

              </ScrollView>
                <View style={styles.wrapDot}>
                  {
                    images.map((e, index) =>
                    <Text 
                    key={e} 
                    style={imageActive == index ? styles.dotActive : styles.dot}
                    >‚óè</Text>
                    )
                  }
                </View>
              </View>

          </SafeAreaView>
      </View>
  )
}

const styles = StyleSheet.create({


      displayContainer: {
        flex: 1,
        display: "flex",
        paddingVertical: "30px",
        alignItems: 'center',
      },

      cardContainer: {
          flexDirection: 'row',
          width: '100%',
          height: '35%',
          backgroundColor: '#FFEDDD',
          padding: '15px',
          borderColor: '#401F02',
          borderWidth: '3px',
          borderLeftWidth: '0px',
          borderRightWidth: '0px',
          gap: 20
      },

      container: {
        flex: 1
      },

      wrap: {
        width: WIDTH,
        height: HEIGHT * 0.25,
      },

      wrapDot: {
        position: 'absolute',
        bottom: 0,
        flexDirection: 'row',
        alignSelf: 'center',
      },
      
      dotActive: {
        margin: 3,
        color: '#6E4119',
      },

      dot: {
        margin: 3,
        color: '#F3C296',

      }
  }
)


export default RoomViewPostScreen
