import { View, Text, StyleSheet, Image, TouchableOpacity, Button, TextInput } from 'react-native'
import React, {useState, useEffect, useContext} from 'react'
import { useNavigation } from '@react-navigation/native'
import { UserInfoContext } from '../context/userInfoContext'
import { storage } from '../../FirebaseConfig'
import { ref, uploadBytes, getDownloadURL } from 'firebase/storage'
import * as ImagePicker from 'expo-image-picker';
import { DataProcessorContext } from '../context/DataProcessor'
import DatePicker from 'react-native-datepicker'

const EditProfile = () => {

    const { 
        currentUser
      } = useContext(DataProcessorContext);

    const [image, setImage] = useState(null);
    const [name, setName] = useState('');
    const [phone, setPhone] = useState("");
    const [whatsapp, setWhatsapp] = useState("");
    const [signUpEmail, setSignUpEmail] = useState('');

    /////// Gender Picker /////
    const [open, setOpen] = useState(false);
    const [value, setValue] = useState("");
    const [items, setItems] = useState([
        {label: 'Male', value: 'Male'},
        {label: 'Female', value: 'Female'}
    ]);

    /////////// Date Picker //////////
    const [selectedDate, setSelectedDate] = useState("");

    /////////// Age Auto Fill Function //////////
    const [age, setAge] = useState("")

    function handleDateChange(date) {

        setSelectedDate(date);

        var currentYear = moment().format("YYYY")
        var userBirthYear = moment(date).format("YYYY")
        var age = Math.abs(currentYear - userBirthYear)

        setAge(age)
    }


    async function uploadImage(imageUri) {
        try {
          const response = await fetch(imageUri)
          const blobFile = await response.blob()

          const reference = ref(storage, "your_name.jpg")
          const result = await uploadBytes(reference, blobFile)
          const url = await getDownloadURL(result.ref)

          return url
        } catch (err) {
          return Promise.reject(err)
      }
    }

  const pickImage = async () => {
    // No permissions request is necessary for launching the image library
    let result = await ImagePicker.launchImageLibraryAsync({
      mediaTypes: ImagePicker.MediaTypeOptions.All,
      allowsEditing: true,
      aspect: [4, 3],
      quality: 1,
    });

    if (!result.cancelled) {
      setImage(result.uri);
      uploadImage(result.uri).then(url => {
        console.log(url)
      })
    }
  };

  const handleSaveProfile = () => {

  }

  return (
    <View style={styles.container}>
        {image ? 
            <Image source={{ uri: image }} resizeMode='cover' style={{aspectRatio: 1, width: '40%', borderWidth: '2px', borderRadius: '100%' }}/>
                :
            <Image resizeMode='cover' style={{aspectRatio: 1, width: '40%', borderWidth: '2px', borderRadius: '100%' }} source={{
            uri: currentUser?.userProfile
            }}/>
        }
        <Button title="Choose an Image" onPress={pickImage} />

        <View style={styles.content}>
            <View style={{zIndex: 1000}}>
                <Text style={{paddingVertical: 5}}>Date Of Birth:</Text>
                <DatePicker style={styles.textInputDob}
                    selected={selectedDate} 
                    onChange={(date) => handleDateChange(date)}
                    dateFormat="dd/MM/yyyy" 
                    maxDate={new Date()} 
                    showYearDropdown
                    showMonthDropdown
                    />
            </View>

            <View style={{flexDirection: 'row', gap: 10, zIndex: 999}}>

            <View style={{padding: 0}}>
                <Text style={{paddingVertical: 5}}>Age:</Text>
                <Text style={styles.textInputAge}>{age ? age : 0}</Text>
            </View>

            <View>
                <Text style={{paddingVertical: 5}}>Gender:</Text>
                <DropDownPicker style={styles.textInputGender}
                                open={open}
                                value={value}
                                items={items}
                                setOpen={setOpen}
                                setValue={setValue}
                                setItems={setItems}
                                />
            </View>
            </View>

            <View style={{padding: 0}}>
              <Text style={{paddingVertical: 5}}>Email:</Text>
              <TextInput style={styles.textInput} keyboardType='email-address' onChangeText={setSignUpEmail}></TextInput>
            </View>

            <View style={{padding: 0}}>
              <Text style={{paddingVertical: 5}}>Phone No:</Text>
              <TextInput style={styles.textInput} onChangeText={setPhone} keyboardType='numeric' textContentType='telephoneNumber'></TextInput>
            </View>

            <View style={{padding: 0}}>
              <Text style={{paddingVertical: 5}}>Whatsapp No:</Text>
              <TextInput style={styles.textInput} onChangeText={setWhatsapp} keyboardType='numeric' textContentType='telephoneNumber'></TextInput>
            </View>

            <View style={{padding: 0}}>
                <Text style={{paddingVertical: 5}}>Username:</Text>
                <TextInput style={styles.textInput} onChangeText={setName}></TextInput>
            </View>
        </View>

        <TouchableOpacity onPress={()=> { handleSaveProfile() }} style={styles.saveButton}>
            <Text style={{color:'#000000', fontSize: '20px', fontWeight: '500'}}>SAVE</Text>
        </TouchableOpacity>

    </View>
  );



}

const styles = StyleSheet.create({

    container: {
        flex: 1,
        height:'100%',
        width:'100%',
        overflow: 'scroll',
        display: "flex",
        paddingTop: "20px",
        paddingBottom: '20px',
        alignItems: 'center',
        gap: 20
      },

      flexDirection: {
        flexDirection: 'row',
        gap: 15,
      },

      content: {
        paddingVertical: '10px',
        display: 'flex',
        flexDirection: 'column',
        alignContent: 'center'
      },

      textInput:{
        borderWidth: 1,
        borderColor: '#401F02',
        paddingLeft: 5,
        backgroundColor: 'white',
        height: 21
      },

      saveButton: {
        width: '40%', 
        height: '40px',
        alignItems: 'center',
        justifyContent: 'center',
        backgroundColor: '#ECD0B8',
        borderRadius: '20px',
        borderColor: 'black',
        borderWidth: '1px',
        shadowColor: "#000",
        shadowOffset: {
          width: 10,
          height: 10,
        },
        shadowOpacity: 0.36,
        shadowRadius: 7,
        elevation: 11,
      },

  })


export default EditProfile;
