import { View, Text, StyleSheet, Image,TouchableOpacity } from 'react-native'
import React, {useState, useEffect, useContext} from 'react'
import { useNavigation } from '@react-navigation/native'
import Ionicons from 'react-native-vector-icons/Ionicons'
import Entypo from 'react-native-vector-icons/Entypo'
import { WhatsAppOutlined } from '@ant-design/icons'
import { auth } from '../../FirebaseConfig'
import { signOut } from "firebase/auth";
import { UserInfoContext } from '../context/userInfoContext'
import { DataProcessorContext } from '../context/DataProcessor'
import { getAuth, sendPasswordResetEmail } from "firebase/auth";


const ProfileScreen = () => {

  const navigation = useNavigation();

  const { 
    currentUser
  } = useContext(DataProcessorContext);

  function handleLogin(){
    signOut(auth).then(() => {
      // Sign-out successful.
      console.log('Logout Successful')
    }).catch((error) => {
      // An error happened.
    });
  }

  /////////// Change Password ///////////
  const auth = getAuth();
  sendPasswordResetEmail(auth, changePassword)
    .then(() => {
      // Password reset email sent!
      // ..
    })
    .catch((error) => {
      const errorCode = error.code;
      const errorMessage = error.message;
      // ..
    });
  
  return (
      <View style={styles.container}> 
        <Image resizeMode='cover' style={{aspectRatio: 1, width: '40%', borderWidth: '2px', borderRadius: '100%' }} source={{
          uri: currentUser?.userProfile
        }}></Image>
        <Text style={{color:'#000000', fontSize: '16px', fontWeight: '700'}}>{currentUser?.userName}</Text>
          <View style={styles.btnsContainer}>

            <TouchableOpacity onPress={()=> { navigation.navigate("EditProfile") }} style={styles.editButton}>
                <View style={styles.flexDirection}>
                  <Ionicons name={'person'} size={25} style={{}}/>
                  <Text style={{color:'#000000', fontSize: '20px', fontWeight: '500'}}>Edit Profile</Text>
                </View>
            </TouchableOpacity>

            <TouchableOpacity onPress={()=> {}} style={styles.policyButton}>
                <View style={styles.flexDirection}>
                  <Ionicons name={'shield-checkmark'} size={27} style={{}}/>
                  <Text style={{color:'#000000', fontSize: '20px', fontWeight: '500'}}>Privacy Policy</Text>
                </View>
            </TouchableOpacity>

            <TouchableOpacity onPress={()=> {}} style={styles.termsButton}>
                <View style={styles.flexDirection}>
                  <Ionicons name={'information-circle'} size={30} style={{}}/>
                  <Text style={{color:'#000000', fontSize: '20px', fontWeight: '500'}}>Terms & Conditions</Text>
                </View>
            </TouchableOpacity>

            <TouchableOpacity onPress={()=> {changePassword()}} style={styles.changePassword}>
                <View style={styles.flexDirection}>
                  <Ionicons name={'lock'} size={30} style={{}}/>
                  <Text style={{color:'#000000', fontSize: '20px', fontWeight: '500'}}>Change Password</Text>
                </View>
            </TouchableOpacity>

            <TouchableOpacity onPress={()=>  handleLogin() } style={styles.logoutButton}>
                <View style={styles.flexDirection}>
                  <Ionicons name={'log-out-outline'} size={30} style={{}}/>
                  <Text style={{color:'#000000', fontSize: '20px', fontWeight: '500'}}>Logout</Text>
                </View>
            </TouchableOpacity>

          </View>
      </View>
  )
}

const styles = StyleSheet.create({
  container: {
      flex: 1,
      height:'100%',
      width:'100%',
      overflow: 'scroll',
      display: "flex",
      paddingTop: "20px",
      paddingBottom: '20px',
      alignItems: 'center',
      gap: 20
    },

    flexDirection: {
      flexDirection: 'row',
      gap: 15,
    },

    btnsContainer: {
      flex: 1,
      height: "100%",
      width:'100%',
      paddingTop: '20px',
      paddingVertical: "20px",
      justifyContent: 'space-evenly',
      alignItems: 'center',
      gap: 10,
    },

    editButton: {
      width: '90%', 
