      paddingLeft: '30px',
      alignItems: 'flex-start', 
      justifyContent: 'center',
      backgroundColor: '#ECD0B8',
      borderRadius: '50px',
      borderColor: 'black',
      borderWidth: '1px',
      shadowColor: "#000",
      shadowOffset: {
        width: 10,
        height: 10,
      },
      shadowOpacity: 0.36,
      shadowRadius: 7,
      elevation: 11,
    },

    policyButton: {
      width: '90%', 
      height: '20%',  
      paddingLeft: '30px',
      alignItems: 'flex-start', 
      justifyContent: 'center',
      backgroundColor: '#ECD0B8',
      borderRadius: '50px',
      borderColor: 'black',
      borderWidth: '1px',
      shadowColor: "#000",
      shadowOffset: {
        width: 10,
        height: 10,
      },
      shadowOpacity: 0.36,
      shadowRadius: 7,
      elevation: 11,
    },

    termsButton: {
      width: '90%', 
      height: '20%',  
      paddingLeft: '30px',
      alignItems: 'flex-start', 
      justifyContent: 'center',
      backgroundColor: '#ECD0B8',
      borderRadius: '50px',
      borderColor: 'black',
      borderWidth: '1px',
      shadowColor: "#000",
      shadowOffset: {
        width: 10,
        height: 10,
      },
      shadowOpacity: 0.36,
      shadowRadius: 7,
      elevation: 11,
    },

    logoutButton: {
      width: '90%', 
      height: '20%',  
      paddingLeft: '30px',
      alignItems: 'flex-start', 
      justifyContent: 'center',
      backgroundColor: '#ECD0B8',
      borderRadius: '50px',
      borderColor: 'black',
      borderWidth: '1px',
      shadowColor: "#000",
      shadowOffset: {
        width: 10,
        height: 10,
      },
      shadowOpacity: 0.36,
      shadowRadius: 7,
      elevation: 11,
    },

    changePassword: {
      width: '90%', 
      height: '20%',  
      paddingLeft: '30px',
      alignItems: 'flex-start', 
      justifyContent: 'center',
      backgroundColor: '#ECD0B8',
      borderRadius: '50px',
      borderColor: 'black',
      borderWidth: '1px',
      shadowColor: "#000",
      shadowOffset: {
        width: 10,
        height: 10,
      },
      shadowOpacity: 0.36,
      shadowRadius: 7,
      elevation: 11,
    },



  }
)

export default ProfileScreen
