import react, {useState} from 'react'

const image = [
  'https://www.google.com/url?sa=i&url=https%3A%2F%2Fwww.medicalnewstoday.com%2Farticles%2F321450&psig=AOvVaw3AAVcW7E_1OlLCzVeDtw9S&ust=1667839801313000&source=images&cd=vfe&ved=0CBAQjRxqFwoTCLjHv62PmvsCFQAAAAAdAAAAABAE',
