  'https://www.google.com/url?sa=i&url=https%3A%2F%2Fhealth.clevelandclinic.org%2Fhow-to-relieve-stress%2F&psig=AOvVaw3AAVcW7E_1OlLCzVeDtw9S&ust=1667839801313000&source=images&cd=vfe&ved=0CBAQjRxqFwoTCLjHv62PmvsCFQAAAAAdAAAAABAU'
]

const WIDTH = Dimensions.get('window').width
const HEIGHT = Dimensions.get('window').height

const RoomViewPostScreen = () => {
  const [imageActive, setimageActive] = useState(0)

  onchange = (nativeEvent) => {
    if (nativeEvent) {
      const slide = Math.ceil(nativeEvent.contentOffset.x / nativeEvent.layoutmeasurement.width)
      if (slide != imageActive) {setimageActive(slide)}
    }
  }

  return (
    <SafeAreaView style= {styles.container}>
        <View style= {styles.wrap}>
        <ScrollView
          onScroll={({nativeEvent}) => onchange(nativeEvent)}
          showsHorizontalScrollIndicator={false}
          pagingEnabled
          horizontal
          style={styles.wrap}
          >

          {
            images.map((e, index) => 
            
            <Image 
              key={e}
              resizeMode='stretch'
              style={styles.wrap}
              source={{uri: e}}
              
              />
              
            
            )
          }

        </ScrollView>
          <View style={styles.wrapDot}>
            {
              images.map((e, index) =>
              <Text 
              key={e} 
              style={imageActive == index ? styles.dotActive : styles.dot}
              
              >‚óè</Text>

              )
            }
          </View>
        </View>

    </SafeAreaView>
  )
}

const styles = StyleSheet.create({

      container: {
        flex: 1
      },

      wrap: {
        width: WIDTH,
        height: HEIGHT * 0.25,
      },

      wrapDot: {
        position: 'absolute',
        bottom: 0,
        flexDirection: 'row',
        alignSelf: 'center',
      },
      
      dotActive: {
        margin: 3,
        color: 'black',
      },

      dot: {
        margin: 3,
        color: 'grey',

      }


  }
)



export default RoomViewPostScreen
