import React, { createContext, useEffect, useState, useReducer, useContext, useRef } from "react";
import { auth, db } from "../../FirebaseConfig";
import { onAuthStateChanged } from "firebase/auth";
import { doc, getDoc } from "firebase/firestore";

export const UserInfoContext = createContext();

export const UserInfoContextProvider = ({ children }) => {

    const [loginState, setLoginState] = useState(false)
    const [userID, setUserID] = useState('');
    const [currentUser, setCurrentUser] = useState(null);

    useEffect(() => {
        const unsub = onAuthStateChanged(auth, async (user) => {
            if (user) {
                // User is signed in, see docs for a list of available properties
                // https://firebase.google.com/docs/reference/js/firebase.User
                const uid = user.uid;
                setLoginState(true)


            } else {
                // User is signed out
                // ...
                setLoginState(false)
            }
            });

        return () => {
            unsub()
        }
    },[])

    useEffect(async () => {
        if(userID){
            const docRef = doc(db, "users", userID);
            const docSnap = await getDoc(docRef);

            if (docSnap.exists()) {
                setCurrentUser(docSnap.data());
            } else {
            // doc.data() will be undefined in this case
            console.log("No such document!");
            }
        }
    },[userID])


    ////// Dispatch X-AXIS //////
    const INITIAL_X_AXIS = [];

    const xAxisReducer = (state, action) => {
        switch(action.type){
            case "CHANGE_X_AXIS":
                return action.payload
        };
    };

    const [xAxis, dispatchXAxis] = useReducer(xAxisReducer, INITIAL_X_AXIS);
    /////////////////////////////

    return (
        <UserInfoContext.Provider value={{
            loginState: loginState,
            currentUser: currentUser
        }}>
            {children}
        </UserInfoContext.Provider>
    );

};
