import { View, Text, StyleSheet, Image, TextInput, TouchableOpacity } from 'react-native'
import React, {useState, useEffect} from 'react'
import AntDesignIcon from 'react-native-vector-icons/FontAwesome'
import { useNavigation } from '@react-navigation/native'
import FontAwesome5 from 'react-native-vector-icons/FontAwesome5'
import MaterialIcons from 'react-native-vector-icons/MaterialIcons'

const HomeScreen = () => {

  return (
    <View style={styles.container}>
      <View style={styles.WelcomeContainer}>
        <Text style={styles.WelcomeText}>Welcome to Roomarch</Text>
        <View>
          <FontAwesome5 name='user-friends' size={35} style={{}}/>
          <MaterialIcons name='home' size={50} style={{}}/>
        </View>
      </View>
      <Text style={styles.roommateText}>Find Roommate</Text>
      <View style={styles.roommateOptionContainer}>
          <Image style={styles.roommateimageContainer} source={require('../assets/SigninBackground.jpg')}></Image>
      </View>
    </View>
    
  )
}

const styles = StyleSheet.create({
  container: {
    display: "flex",
    flex: 1,
    paddingVertical: "30px"
  },
  WelcomeContainer: {
    width: '100%',
    height: '30%',
    backgroundColor: '#F5C79F',
    paddingVertical: '15px'
  },
  WelcomeText: {
    fontSize: '24px',
    fontWeight: 'bold',
    textAlign: 'center'
  },
  roommateOptionContainer: {
    width: '80%',
    height: '30%',
    backgroundColor: '#F5C79F',
    borderTopLeftRadius: '20px',
    borderTopRightRadius: '20px',
    paddingVertical: '30px',
    marginHorizontal: '30px'
  },
  roommateText: {
    fontSize: '16px',
    fontWeight: 'bold',
    textAlign: 'center',
    paddingVertical: '30px'
  },
  roommateimageContainer: {
    flex: 1,
    alignContent: 'flex-start'
  },

});

export default HomeScreen
