import React, { useState, useContext } from 'react';
import LoginScreen from './src/containers/LoginScreen';
import HomeScreen from './src/containers/HomeScreen';
import RoommateViewPostScreen from './src/containers/RoommateViewPostScreen';
import RoomViewPostScreen from './src/containers/RoomViewPostScreen';
import FavouriteScreen from './src/containers/FavouriteScreen';
import ProfileScreen from './src/containers/ProfileScreen';
import Ionicons from 'react-native-vector-icons/Ionicons';
import { NavigationContainer } from '@react-navigation/native';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';
import { createStackNavigator } from '@react-navigation/stack';
import { UserInfoContextProvider } from './src/context/userInfoContext';
import { UserInfoContext } from './src/context/userInfoContext';

const Stack = createStackNavigator();
const Tab = createBottomTabNavigator();

function LoginStack() {
  return(
    <Stack.Screen  
      name="LoginScreen" 
      component={LoginScreen} 
      options={{headerShown: false}}/>
  )
}

function HomeStack() {
  return (
      <Stack.Navigator >


        <Stack.Screen  
          name="HomeScreen" 
          component={HomeScreen} 
          options={{headerShown: false}}/>

        <Stack.Screen  
          name="RoommateViewPostScreen" 
          component={RoommateViewPostScreen} 
          options={{title: 'Find Roommate',
                    headerTitleAlign: 'center',
                    headerStyle: {
                      backgroundColor: '#D6B598',
                    },
                    headerTintColor: '#000000',
                    headerTitleStyle: {
                      fontSize: '24px',
                      fontWeight: 'bold',
                    },
                    }}/>

        <Stack.Screen  
        name="RoomViewPostScreen" 
        component={RoomViewPostScreen} 
        options={{title: 'Find Room',
                  headerTitleAlign: 'center',
                  headerStyle: {
                    backgroundColor: '#D6B598',
                  },
                  headerTintColor: '#000000',
                  headerTitleStyle: {
                    fontSize: '24px',
                    fontWeight: 'bold',
                  },
                  }}/>

      </Stack.Navigator>

  );
}
function FavouriteStack() {
  return (
      <Stack.Navigator >
        <Stack.Screen  
        name="FavouriteScreen" 
        component={FavouriteScreen} 
        options={{title: 'Favourite', 
                  headerTitleAlign: 'center',
                  headerStyle: {
                    backgroundColor: '#D6B598',
                  },
                  headerTintColor: '#000000',
                  headerTitleStyle: {
                    fontSize: '24px',
                    fontWeight: 'bold',
                  }, 
                  }}/>
      </Stack.Navigator>

  );
}
function ProfileStack() {
  return (
      <Stack.Navigator >
        <Stack.Screen  
        name="ProfileScreen" 
        component={ProfileScreen} 
        options={{title: 'Profile', 
                  headerTitleAlign: 'center',
                  headerStyle: {
                    backgroundColor: '#D6B598',
                  },
                  headerTintColor: '#000000',
                  headerTitleStyle: {
                    fontSize: '24px',
                    fontWeight: 'bold',
                  }, 
                  }}/>
      </Stack.Navigator>

  );
}

export default function App() {

  return (
    <UserInfoContextProvider>
      <NavigationContainer>

        <Tab.Navigator
          screenOptions={({ route }) => ({ 
            headerShown: false,
            tabBarIcon: ({ focused, color, size }) => {

              let iconName;

              if (route.name === 'Home') {
                iconName = "home"
              } else if (route.name === 'Favourite') {
                iconName = "star"
              } else if (route.name === 'Profile') {
                iconName = "person"
              };

              // You can return any component that you like here!
              return <Ionicons name={iconName} size={size} color={color} />;

            },
            tabBarActiveTintColor: '#6E4119',
            tabBarInactiveTintColor: 'black',
            tabBarInactiveBackgroundColor: '#FFEDDD',
            tabBarActiveBackgroundColor: '#D6B598',

          })}
        >
              <Tab.Screen name="Profile" component={ProfileStack} />
              <Tab.Screen name="Home" component={HomeStack} />
              <Tab.Screen name="Favourite" component={FavouriteStack} />

              <Tab.Screen name="Login" component={LoginStack}/>

        </Tab.Navigator>
      </NavigationContainer>
    </UserInfoContextProvider>
  );
}
