import { View, Text, StyleSheet, Image,TouchableOpacity, Linking } from 'react-native'
import React, {useState, useEffect, useContext, useLayoutEffect} from 'react'
import { useNavigation } from '@react-navigation/native'
import AntDesignIcon from 'react-native-vector-icons/AntDesign'
import { WhatsAppOutlined } from '@ant-design/icons'
import { DataProcessorContext } from '../context/DataProcessor'
import { auth, storage, db } from '../../FirebaseConfig'
import { doc, setDoc, deleteDoc, collection, onSnapshot } from 'firebase/firestore'
import { v4 as uuid } from 'uuid'
import moment from 'moment'


const RoommateDetails = () => {

// page navagation
const navigation = useNavigation();

// const {
//     selectedRoommate
//   } = useContext(DataProcessorContext);

const [data, setData]=useState([])

  useLayoutEffect(() => {
      const ref=collection(db, "roommates")
      onSnapshot(ref, (roommates) =>
        setData(roommates.docs.map((roommates) => ({
          id: roommates.id,
          data: roommates.data()
        }))
        )
      
      )
  })


  return (
    <View>
        {data.map((item, Index) => (
          <View key={Index} style={{

          }}>
            <Image source={{
              uri: item.data.profilePic
            }} />
          </View>
        ))}
    </View>
  )
}


const styles = StyleSheet.create({

  container: {
    flex: 1,
    display: "flex",
    alignItems: 'center',
  },


})

export default RoommateDetails
