import { View, Text, StyleSheet, Image,TouchableOpacity } from 'react-native'
import React, {useState, useEffect} from 'react'
import { useNavigation } from '@react-navigation/native'

const RoommateViewPostScreen = () => {

// page navagation
const navigation = useNavigation();

  return (

    
    <View style={styles.container}>
      <Text style={styles.findRoommateTitle}>Find Roommate</Text>
      <View style={styles.roommateProfileContainer}>
        <View style={styles.profileImageContainer}>
        <Image resizeMode='cover{' style={{aspectRatio: 1, width: '100%', borderWidth: '3.5px' }} source={require('../assets/room.jpg')}></Image>
        </View>
        
        <View style={styles.profileDetailsContainer} >
        <Text>enter details</Text>
        </View>
      </View>
    </View>
  )
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        display: "flex",
        paddingVertical: "30px",
        alignItems: 'center',
      },
    
    findRoommateTitle: {
        fontSize: '24px',
        fontWeight: 'bold',
        textAlign: 'center',
        paddingBottom: '30px'
    },

    roommateProfileContainer: {
        flexDirection: 'row',
        width: '100%',
        height: '35%',
        backgroundColor: '#FFEDDD',
        padding: '15px',
        borderColor: '#401F02',
        borderWidth: '3px',
        borderLeftWidth: '0px',
        borderRightWidth: '0px',
        gap: 20
      },
    
    profileImageContainer: {
      flex: 0.7,
      justifyContent: 'center',
      backgroundColor:'blue'
  },

    profileDetailsContainer: {
      flex: 1,
      backgroundColor:'red',
      paddingTop: "15px",
      paddingBottom: "15px",
      paddingLeft: "5px",
      paddingRight: "5px",
  },

})


export default RoommateViewPostScreen
