import { View, Text, StyleSheet, ImageBackground, TextInput, TouchableOpacity, FlatList } from 'react-native'
import React, {useState, useEffect} from 'react'
import AntDesignIcon from 'react-native-vector-icons/AntDesign'
import { useNavigation } from '@react-navigation/native'
import { signInWithEmailAndPassword, createUserWithEmailAndPassword } from "firebase/auth";
import { auth } from '../../FirebaseConfig';
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import DropDownPicker from 'react-native-dropdown-picker';
import moment from 'moment';
import { FirebaseError } from 'firebase/app';

const LoginScreen = () => {

  const navigation = useNavigation();

  const [open, setOpen] = useState(false);
  const [value, setValue] = useState(null);
  const [items, setItems] = useState([
    {label: 'Male', value: 'Male'},
    {label: 'Female', value: 'Female'}
  ]);

  const [selectedDate, setSelectedDate] = useState("");

  const [age, setAge] = useState("")

  function handleDateChange(date) {setSelectedDate(date);

    var currentYear = moment().format("YYYY")
    var userBirthYear = moment(date).format("YYYY")
    var age = Math.abs(currentYear - userBirthYear)
    
    setAge(age)
  }


  const [signInEmail, setSignInEmail] = useState("");
  const [signInPassword, setSignInPassword] = useState("");
  const [errMessage, setErrMessage] = useState("");
  const [registerErrorMessage, setRegisterErrorMessage] = useState("");

  function SignIn() {

      signInWithEmailAndPassword(auth, signInEmail, signInPassword)
        .then((userCredential) => {
          console.log('Signed In')
        })
        .catch((error) => {
          const err = error.message;
          const one = err.replace('Firebase: Error', '')
          const two = one.replace('(auth/', '')
          const three = two.replace(').', '')
          const newErr = three.replace('-', ' ')
          setErrMessage(newErr)
        });
  }

const [isSignUp, setIsSignUp] = useState (false);
const [signUpEmail, setSignUpEmail] = useState('');
const [signUpPassword, setSignUpPassword] = useState('');
const [signUpName, setIsSignUpName] = useState ('');
const [signUpDateOfBirth, setIsSignUpDateOfBirth] = useState ('');
const [signUpAge, setIsSignUpAge] = useState ('');
const [signUpGender, setIsSignUpGender] = useState ('');
const [signUpPhoneNo, setIsSignUpPhoneNo] = useState ('');
const [signUpWhatappNo, setIsSignUpWhatappNo] = useState ('');
const [signUpConfirmPassword, setIsSignUpConfirmPassword] = useState ('');



signUpUser = async (
  signUpEmail,
  signUpPassword,
  signUpName,
  signUpDateOfBirth,
  signUpAge,
  signUpGender,
  signUpPhoneNo,
  signUpWhatappNo,
  signUpConfirmPassword) => {
    await FirebaseError.auth().createUserWithEmailAndPassword(signUpEmail,signUpPassword,signUpConfirmPassword)
    .then(() => {
      firebase.auth().currentUser.sendEmailVerification({
        handleCodeInApp: true,
        url: 'https://fyp2022-9b63a.firebaseapp.com',
      })
      .then(() =>{
        alert('verification Email sent')
      }).catch((error) => {
        alert(error.message)
      })
      .then(() => {
        firebase.firestore().collection('users')
        .doc(firebase.auth().currentUser.uid)
        .set({
          signUpEmail,
          signUpPassword,
          signUpName,
          signUpDateOfBirth,
          signUpAge,
          signUpGender,
          signUpPhoneNo,
          signUpWhatappNo,
          signUpConfirmPassword
        })
      })
      .catch((error) => {
        alert(error.message)
      })
    })
    .catch((error => {
      alert(error.message) 
    }))
  }

// function SignUp() {
//   createUserWithEmailAndPassword(auth, signUpEmail, signUpPassword)
//   .then((userCredential) => {
//     console.log('Signed Up')
//   })
//   .catch((error) => {
//     const err = error.message;
//     const one = err.replace('Firebase: Error', '')
//     const two = one.replace('(auth/', '')
//     const three = two.replace(').', '')
//     const newErr = three.replace('-', ' ')
//     setRegisterErrorMessage(newErr)
//   });
// }



////// Sign in page //////
  return (
    <View style={styles.container}>
      <ImageBackground style={styles.imageContainer} source={require('../assets/SigninBackground.jpg')}>
        { isSignUp === false ?
        <View style={styles.subContainer}>
        <View style={{padding: 0}}>
            <Text style={{paddingVertical: 5, fontSize: 20, fontWeight:'800' , color:'#401F02'}}>Roomarch</Text>
          </View> 
          <View style={{padding: 0}}>
            <Text style={{paddingVertical: 5}}>Email:</Text>
            <TextInput style={styles.textInput} onChangeText={setSignInEmail}></TextInput>
          </View>
          <View style={{padding: 0}}>
            <Text style={{paddingVertical: 5}}>Password:</Text>
            <TextInput style={styles.textInput} secureTextEntry={true} onChangeText={setSignInPassword}></TextInput>
          </View>
          {errMessage !== "" && <Text>{errMessage}</Text>}
          <View style={{padding: 0}}>
            <TouchableOpacity style={styles.signInButton} onPress={()=> SignIn() } >
              <Text style={{color:'#FFFFFF'}}>Sign In</Text>
            </TouchableOpacity>
          </View>
          <View style={{padding: 0, flexDirection: 'row'}}>
              <Text style={{color:'#000000'}}>New User?</Text>
              <TouchableOpacity style={{}} onPress={() => {
                setIsSignUp(true)
              }}>
              <Text style={{paddingLeft: 5, color:'#0A1172'}}>Sign Up</Text>
            </TouchableOpacity>
          </View>
          <View style={{padding: 0}}>
              <TouchableOpacity style={{}}>
              <Text style={{ color:'#0A1172'}}>Forget Password?</Text>
            </TouchableOpacity>
          </View>
        </View>


        :

////// sign up page //////
        <View style={styles.signUpContainer}>
          <View style={{height: '10%', width: '100%', flexDirection: 'row', alignItems: 'center'}}>
            <View style={{width: '40%'}}>
              <TouchableOpacity onPress={() => { setIsSignUp(false)}}>
              <AntDesignIcon name={'arrowleft'} size={25} style={{paddingLeft: 5}}/>
              </TouchableOpacity>
            </View>
            <View style={{alignItems: 'center', justifyContent: 'center'}}>
              <Text style={{fontWeight:'bold', fontSize:'20px'}}>Sign Up</Text>
            </View>
          </View>
          <View style={{height: '90%', alignItems: 'center', justifyContent: 'space-evenly'}}>
            <View style={{padding: 0}}>
              <Text style={{paddingVertical: 5}}>Name:</Text>
              <TextInput style={styles.textInput} onChangeText={(signUpName) => setIsSignUpName(signUpName)}></TextInput>
            </View>
            <View style={{zIndex: 1000}}>
              <Text style={{paddingVertical: 5}}>Date Of Birth:</Text>
              <DatePicker style={styles.textInputDob}
                          selected={selectedDate} 
                          onChange={(date) => handleDateChange(date)}
                          dateFormat="dd/MM/yyyy" 
                          maxDate={new Date()} 
                          showYearDropdown
                          showMonthDropdown
                          onChangeText={(signUpDateOfBirth) => setIsSignUpDateOfBirth(signUpDateOfBirth)}
                          />
            </View>
            <View style={{flexDirection: 'row', gap: 10, zIndex: 999}}>
                <View style={{padding: 0}}>
                  <Text style={{paddingVertical: 5}}>Age:</Text>
                  <Text style={styles.textInputAge} onChangeText={(signUpAge) => setIsSignUpAge(signUpAge)}>{age ? age : 0}</Text>
                </View>

                <View>
                  <Text style={{paddingVertical: 5}}>Gender:</Text>
                  <DropDownPicker style={styles.textInputGender}
                                  open={open}
                                  value={value}
                                  items={items}
                                  setOpen={setOpen}
                                  setValue={setValue}
                                  setItems={setItems}
                                  onChangeText={(signUpGender) => setIsSignUpGender(signUpGender)}
                                  />
                </View>
            </View>
            <View style={{padding: 0}}>
              <Text style={{paddingVertical: 5}}>Email:</Text>
              <TextInput style={styles.textInput} keyboardType='email-address' onChangeText={setSignUpEmail}></TextInput>
            </View>
            <View style={{padding: 0}}>
              <Text style={{paddingVertical: 5}}>Phone No:</Text>
              <TextInput style={styles.textInput} keyboardType='number-pad' onChangeText={setIsSignUpPhoneNo} textContentType='telephoneNumber'></TextInput>
            </View>
            <View style={{padding: 0}}>
              <Text style={{paddingVertical: 5}}>Whatsapp No:</Text>
              <TextInput style={styles.textInput} keyboardType='number-pad' onChangeText={setIsSignUpWhatappNo} textContentType='telephoneNumber'></TextInput>
            </View>
            <View style={{padding: 0}}>
              <Text style={{paddingVertical: 5}}>Password:</Text>
              <TextInput style={styles.textInput} secureTextEntry={true} onChangeText={setSignUpPassword}></TextInput>
            </View>
            <View style={{padding: 0}}>
              <Text style={{paddingVertical: 5}}>Confirm Password:</Text>
              <TextInput style={styles.textInput} secureTextEntry={true} onChangeText={setIsSignUpConfirmPassword}></TextInput>
            </View>
            <View style={{padding: 15}}>
              <TouchableOpacity style={styles.signUpButton} onPress={() => { SignUp() }}>
                <Text style={{color:'#FFFFFF'}}>Sign Up</Text>
              </TouchableOpacity>
              {registerErrorMessage !== "" && <Text>{registerErrorMessage}</Text>}
          </View>
          </View>
        </View>
        }
      </ImageBackground>
    </View>
  )
}

