import { View, Text, StyleSheet, Image,TouchableOpacity, Linking, Button } from 'react-native'
import React, {useState, useEffect, useContext} from 'react'
import { useNavigation } from '@react-navigation/native'
import AntDesignIcon from 'react-native-vector-icons/AntDesign'
import { WhatsAppOutlined } from '@ant-design/icons'
import { DataProcessorContext } from '../context/DataProcessor'

const RoommateDetails = () => {

// page navagation
const navigation = useNavigation();

const {
    selectedRoommate
  } = useContext(DataProcessorContext);

  const [openGender, setOpenGender] = useState(false)
  const [gender, setGender] = useState([
      {label: 'Male', value: 'Male'},
      {label: 'Female', value: 'Female'}
  ]);

  const [openRoomType, setOpenRoomType] = useState(false)
  const [roomType, setRoomType] = useState([
      {label: 'Master Bedroom', value: 'Master Bedroom'},
      {label: 'Single Bedroom', value: 'Single Bedroom'},
      {label: 'Double Bedroom', value: 'Double Bedroom'},
  ]);

  const [openFurnishType, setOpenFurnishType] = useState(false)
  const [furnishType, setFurnishType] = useState([
      {label: 'Fully Furnished', value: 'Fully Furnished'},
      {label: 'Partly Furnished', value: 'Partly Furnished'},
      {label: 'Non Furnished', value: 'Non Furnished'},
  ]);

  const [openPreferGender, setOpenPreferGender] = useState(false)
  const [preferGender, setPreferGender] = useState([
      {label: 'Male', value: 'Male'},
      {label: 'Female', value: 'Female'}
  ]);

  return (
    <>
      {selectedRoommate &&
        <View style={styles.container}>

          <Image source={{ uri: selectedRoommate.profilePic }} resizeMode='cover' style={{aspectRatio: 1, width: '40%', borderWidth: '2px', borderRadius: '100%' }}/>

          <Button title="Choose an Image" onPress={() => {}}/>

          <View style={styles.inputContainer}>
            <Text style={{paddingVertical: 5}}>Title:</Text>
            <TextInput style={styles.textInput} onChange={(e) => {  }} value={selectedRoommate.title}></TextInput>
          </View>

          <View style={styles.inputContainer}>
            <Text style={{paddingVertical: 5}}>Name:</Text>
            <TextInput style={styles.textInput} onChange={(e) => {  }} value={selectedRoommate.name}></TextInput>
          </View>

          <View style={styles.inputContainer}>
            <Text style={{paddingVertical: 5}}>Age:</Text>
            <TextInput keyboardType='numeric' style={styles.textInput} onChange={(e) => {  }} value={selectedRoommate.age}></TextInput>
          </View>

          <View style={[styles.inputContainer, { zIndex: 999 }]}>
            <Text style={{paddingVertical: 5}}>Gender:</Text>
            <DropDownPicker style={styles.doprdownTextInput}
                containerStyle={{ zIndex: 10000 }}
                open={openGender}
                value={selectedRoommate.gender}
                items={gender}
                setOpen={setOpenGender}
                onSelectItem={(item) => {

                }}
                setItems={setGender}
                zIndex={1000}
                />
          </View>

          <View style={styles.inputContainer}>
            <Text style={{paddingVertical: 5}}>Price per Month:</Text>
            <TextInput keyboardType='numeric' style={styles.textInput} onChange={(e) => { }} value={selectedRoommate.price}></TextInput>
          </View>

          <View style={styles.inputContainer}>
            <Text style={{paddingVertical: 5}}>Description:</Text>
            <TextInput style={styles.textInput} onChange={(e) => { }} value={selectedRoommate.description}></TextInput>
          </View>

          <View style={styles.details}>
            <Text>Room Preferences</Text>
            <View style={[styles.inputContainer, { zIndex: 999 }]}>
            <Text style={{paddingVertical: 5}}>Room Type:</Text>
            <DropDownPicker style={styles.doprdownTextInput}
                open={openRoomType}
                value={selectedRoommate.roomType}
                items={roomType}
                setOpen={setOpenRoomType}
                onSelectItem={(item) => {

                }}
                setItems={setRoomType}
                />
            </View>
            <View style={[styles.inputContainer, { zIndex: 998 }]}>
              <Text style={{paddingVertical: 5}}>Furnish Type:</Text>
              <DropDownPicker style={styles.doprdownTextInput}
                  open={openFurnishType}
                  value={selectedRoommate.furnishType}
                  items={furnishType}
                  setOpen={setOpenFurnishType}
                  onSelectItem={(item) => {

                  }}
                  setItems={setFurnishType}
                  />
            </View>
            <View style={styles.inputContainer}>
              <Text style={{paddingVertical: 5}}>Number of People:</Text>
              <TextInput keyboardType='numeric' style={styles.textInput} onChange={(e) => {  }} value={selectedRoommate.numberOfPeople}></TextInput>
            </View>
          </View>

          <View style={styles.details}>
            <Text>Area Preferences</Text>
            <View style={styles.inputContainer}>
              <Text style={{paddingVertical: 5}}>Building:</Text>
              <TextInput style={styles.textInput} onChange={(e) => {  }} value={selectedRoommate.building}></TextInput>
            </View>
            <View style={styles.inputContainer}>
              <Text style={{paddingVertical: 5}}>City:</Text>
              <TextInput style={styles.textInput} onChange={(e) => {  }} value={selectedRoommate.address}></TextInput>
            </View>
            <View style={styles.inputContainer}>
              <Text style={{paddingVertical: 5}}>State:</Text>
              <TextInput style={styles.textInput} onChange={(e) => {  }} value={selectedRoommate.city}></TextInput>
            </View>
          </View>

          <View style={styles.details}>
            <Text>Other Preferences</Text>
            <View style={styles.inputContainer}>
              <Text style={{paddingVertical: 5}}>Household Number:</Text>
              <TextInput style={styles.textInput} onChange={(e) => {  }} value={selectedRoommate.householdNumber}></TextInput>
            </View>
            <View style={[styles.inputContainer, { zIndex: 999 }]}>
              <Text style={{paddingVertical: 5}}>Preferred Gender:</Text>
              <DropDownPicker style={styles.doprdownTextInput}
                  zIndex={1000}
                  open={openPreferGender}
                  value={selectedRoommate.preferredGender}
                  items={preferGender}
                  setOpen={setOpenPreferGender}
                  onSelectItem={(item) => {

                  }}
                  setItems={setPreferGender}
                  />
            </View>
            <View style={styles.inputContainer}>
              <Text style={{paddingVertical: 5}}>Preferred Age:</Text>
              <TextInput keyboardType='numeric' style={styles.textInput} onChange={(e) => {  }} value={selectedRoommate.preferredAge}></TextInput>
            </View>
            <View style={styles.inputContainer}>
              <Text style={{paddingVertical: 5}}>Occupation Status:</Text>
              <TextInput style={styles.textInput} onChange={(e) => {  }} value={selectedRoommate.occupationStatus}></TextInput>
            </View>
            <View style={styles.inputContainer}>
              <Text style={{paddingVertical: 5}}>Preferred Pet:</Text>
              <TextInput style={styles.textInput} onChange={(e) => {  }} value={selectedRoommate.preferredPet}></TextInput>
            </View>
          </View>

          <View style={styles.inputContainer}>
            <Text style={{paddingVertical: 5}}>Whatsapp No:</Text>
            <TextInput keyboardType='numeric' style={styles.textInput} onChange={(e) => {  }} value={selectedRoommate.whatsapp}></TextInput>
          </View>

          <TouchableOpacity onPress={()=> { }} style={styles.saveButton}>
            <Text style={{color:'#FFFFFF', fontSize: '16px', fontWeight:'400'}}>Add Roommate</Text>
          </TouchableOpacity>

        </View>
      }
    </>
  )
}


const styles = StyleSheet.create({
  container: {
    flex: 1,
    height:'100%',
    width:'100%',
    overflow: 'scroll',
    display: "flex",
    paddingTop: "20px",
    paddingBottom: '20px',
    alignItems: 'center',
    gap: 10
  },
  flexDirection: {
    flexDirection: 'row',
    gap: 15,
  },
  inputContainer: {
    width: '80%'
  },
  details: {
    paddingVertical: '10px',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    width: '80%'
  },
  textInput:{
    display: 'flex',
    flexDirection: 'row',
    alignItems: 'center',
    width: '100%',
    borderWidth: 1,
    borderColor: '#401F02',
    paddingLeft: 5,
    backgroundColor: 'white',
    height: 30
  },
  doprdownTextInput:{
    display: 'flex',
    flexDirection: 'row',
    alignItems: 'center',
    width: '100%',
    borderWidth: 1,
    borderColor: '#401F02',
    paddingLeft: 5,
    backgroundColor: 'white',
    height: 30,
    zIndex: 10000,
  },
  saveButton: {
    width: '40%', 
    height: '40px',
    alignItems: 'center',
    justifyContent: 'center',
    backgroundColor: '#401F02',
    borderRadius: '20px',
    borderColor: 'black',
    borderWidth: '1px',
    shadowColor: "#000",
    shadowOffset: {
      width: 10,
      height: 10,
    },
    shadowOpacity: 0.36,
    shadowRadius: 7,
    elevation: 11,
  },
})

export default RoommateDetails
