import { View, Text, StyleSheet, Image,TouchableOpacity, Linking, Button, textInput } from 'react-native'
import React, {useState, useEffect, useContext} from 'react'
import { useNavigation } from '@react-navigation/native'
import AntDesignIcon from 'react-native-vector-icons/AntDesign'
import { WhatsAppOutlined } from '@ant-design/icons'
import { DataProcessorContext } from '../context/DataProcessor'
import DropDownPicker from 'react-native-dropdown-picker';
import { v4 as uuid } from 'uuid';


const RoomDetails = () => {

// page navagation
const navigation = useNavigation();

const {
    selectedRoom
  } = useContext(DataProcessorContext);
  
  const [openRoomType, setOpenRoomType] = useState(false)
  const [roomType, setRoomType] = useState([
      {label: 'Master Bedroom', value: 'Master Bedroom'},
      {label: 'Single Bedroom', value: 'Single Bedroom'},
      {label: 'Double Bedroom', value: 'Double Bedroom'},
  ]);

  const [openFurnishType, setOpenFurnishType] = useState(false)
  const [furnishType, setFurnishType] = useState([
      {label: 'Fully Furnished', value: 'Fully Furnished'},
      {label: 'Partly Furnished', value: 'Partly Furnished'},
      {label: 'Non Furnished', value: 'Non Furnished'},
  ]);

  const [openPreferGender, setOpenPreferGender] = useState(false)
  const [preferGender, setPreferGender] = useState([
      {label: 'Male', value: 'Male'},
      {label: 'Female', value: 'Female'}
  ]);
  return (
        <>
          {selectedRoom &&
            <View style={styles.container}>
            
              <Image source={{ uri: selectedRoom.uri }} resizeMode='cover' style={{aspectRatio: 1, width: '100px', height: '100px', borderWidth: '2px', borderRadius: '15px' }}/>
                
              <Button title="Choose Image" onPress={() => {}} />

              <View style={styles.inputContainer}>
                <Text style={{paddingVertical: 5}}>Title:</Text>
                <TextInput style={styles.textInput} onChange={(e) => { }} value={selectedRoom.title}></TextInput>
              </View>

              <View style={styles.inputContainer}>
                <Text style={{paddingVertical: 5}}>Price per Month:</Text>
                <TextInput keyboardType='numeric' style={styles.textInput} onChange={(e) => { }} value={selectedRoom.price}></TextInput>
              </View>

              <View style={styles.inputContainer}>
                <Text style={{paddingVertical: 5}}>Description:</Text>
                <TextInput style={styles.textInput} onChange={(e) => { }} value={selectedRoom.description}></TextInput>
              </View>
              
              <View style={styles.details}>
                <Text>Room Preferences</Text>
                <View style={[styles.inputContainer, { zIndex: 999 }]}>
                <Text style={{paddingVertical: 5}}>Room Type:</Text>
                <DropDownPicker style={styles.doprdownTextInput}
                    open={openRoomType}
                    value={selectedRoom.roomType}
                    items={roomType}
                    setOpen={setOpenRoomType}
                    onSelectItem={(item) => {
                      
                    }}
                    setItems={setRoomType}
                    />
                </View>
                <View style={[styles.inputContainer, { zIndex: 998 }]}>
                  <Text style={{paddingVertical: 5}}>Furnish Type:</Text>
                  <DropDownPicker style={styles.doprdownTextInput}
                      open={openFurnishType}
                      value={selectedRoom.furnishType}
                      items={furnishType}
                      setOpen={setOpenFurnishType}
                      onSelectItem={(item) => {
                        
                      }}
                      setItems={setFurnishType}
                      />
                </View>
                <View style={styles.inputContainer}>
                  <Text style={{paddingVertical: 5}}>Number of People:</Text>
                  <TextInput keyboardType='numeric' style={styles.textInput} onChange={(e) => {  }} value={selectedRoom.numberOfPeople}></TextInput>
                </View>
              </View>

              <View style={styles.details}>
                <Text>Area Preferences</Text>
                <View style={styles.inputContainer}>
                  <Text style={{paddingVertical: 5}}>Building:</Text>
                  <TextInput style={styles.textInput} onChange={(e) => { }} value={selectedRoom.building}></TextInput>
                </View>
                <View style={styles.inputContainer}>
                  <Text style={{paddingVertical: 5}}>Address:</Text>
                  <TextInput style={styles.textInput} onChange={(e) => { }} value={selectedRoom.address}></TextInput>
                </View>
                <View style={styles.inputContainer}>
                  <Text style={{paddingVertical: 5}}>City:</Text>
                  <TextInput style={styles.textInput} onChange={(e) => { }} value={selectedRoom.city}></TextInput>
                </View>
              </View>

              <View style={styles.details}>
                <Text>Other Preferences</Text>
                <View style={styles.inputContainer}>
                  <Text style={{paddingVertical: 5}}>Household Number:</Text>
                  <TextInput style={styles.textInput} onChange={(e) => { }} value={selectedRoom.householdNumber}></TextInput>
                </View>
                <View style={[styles.inputContainer, { zIndex: 999 }]}>
                  <Text style={{paddingVertical: 5}}>Preferred Gender:</Text>
                  <DropDownPicker style={styles.doprdownTextInput}
                      zIndex={1000}
                      open={openPreferGender}
                      value={selectedRoom.preferredGender}
                      items={preferGender}
                      setOpen={setOpenPreferGender}
                      onSelectItem={(item) => {
                        
                      }}
                      setItems={setPreferGender}
                      />
                </View>
                <View style={styles.inputContainer}>
                  <Text style={{paddingVertical: 5}}>Preferred Age:</Text>
                  <TextInput keyboardType='numeric' style={styles.textInput} onChange={(e) => { }} value={selectedRoom.preferredAge}></TextInput>
                </View>
                <View style={styles.inputContainer}>
                  <Text style={{paddingVertical: 5}}>Occupation Status:</Text>
                  <TextInput style={styles.textInput} onChange={(e) => {  }} value={selectedRoom.occupationStatus}></TextInput>
                </View>
                <View style={styles.inputContainer}>
                  <Text style={{paddingVertical: 5}}>Preferred Pet:</Text>
                  <TextInput style={styles.textInput} onChange={(e) => {  }} value={selectedRoom.preferredPet}></TextInput>
                </View>
              </View>

              <TouchableOpacity onPress={()=> { handleAddRoom() }} style={styles.saveButton}>
                <Text style={{color:'#FFFFFF', fontSize: '16px', fontWeight:'400'}}>Add Room</Text>
              </TouchableOpacity>

            </View>
          }
        </>

  )
}

const styles = StyleSheet.create({

  container: {
    flex: 1,
    height:'100%',
    width:'100%',
    overflow: 'scroll',
    display: "flex",
    paddingTop: "20px",
    paddingBottom: '20px',
    alignItems: 'center',
    gap: 10
  },
  flexDirection: {
    flexDirection: 'row',
    gap: 15,
  },
  inputContainer: {
    width: '80%'
  },
  details: {
    paddingVertical: '10px',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    width: '80%'
  },
  textInput:{
    display: 'flex',
    flexDirection: 'row',
    alignItems: 'center',
    width: '100%',
    borderWidth: 1,
    borderColor: '#401F02',
    paddingLeft: 5,
    backgroundColor: 'white',
    height: 30
  },
  doprdownTextInput:{
    display: 'flex',
    flexDirection: 'row',
    alignItems: 'center',
    width: '100%',
    borderWidth: 1,
    borderColor: '#401F02',
    paddingLeft: 5,
    backgroundColor: 'white',
    height: 30,
    zIndex: 10000,
  },
  saveButton: {
    width: '40%', 
    height: '40px',
    alignItems: 'center',
    justifyContent: 'center',
    backgroundColor: '#401F02',
    borderRadius: '20px',
    borderColor: 'black',
    borderWidth: '1px',
    shadowColor: "#000",
    shadowOffset: {
      width: 10,
      height: 10,
    },
    shadowOpacity: 0.36,
    shadowRadius: 7,
    elevation: 11,
  },

  
})

export default RoomDetails
