import { View, Text, StyleSheet, Image,TouchableOpacity } from 'react-native'
import React, {useState, useEffect} from 'react'
import { useNavigation } from '@react-navigation/native'
import AntDesignIcon from 'react-native-vector-icons/AntDesign'
import { WhatsAppOutlined } from '@ant-design/icons'


const ProfileScreen = () => {
  return (
      <View style={styles.container}>
        <View style={styles.imgContainer}>
          <Image resizeMode='cover' style={{aspectRatio: 1, width: '100%', borderWidth: '2px', borderRadius: '100%' }} source={require('../assets/room.jpg')}></Image>
        </View>
           <TouchableOpacity onPress={()=>navigation.navigate('HomeScreen')} style={{
                width: '90%', 
                height: '15%',  
                paddingLeft: '30px',
                alignItems: 'flex-start', 
                justifyContent: 'center',
                backgroundColor: '#401F02',
                borderRadius: '50px',
                shadowColor: "#000",
                shadowOffset: {
                  width: 10,
                  height: 10,
                },
                shadowOpacity: 0.36,
                shadowRadius: 6.68,
                elevation: 11,
              }}>
                <View style={styles.flexDirection}>
                  <AntDesignIcon name={'hearto'} size={25} style={{}}/>
                  <Text style={{color:'#FFFFFF', fontSize: '20px'}}>View Details</Text>
                </View>
            </TouchableOpacity>
        </View>
  )
}

const styles = StyleSheet.create({
  container: {
      flex: 1,
      display: "flex",
      paddingVertical: "30px",
      alignItems: 'center',
    },

    imgContainer: {
      flex: 1,
      height: "40%",
      width:'40%',
    },

    flexDirection: {
      flexDirection: 'row',
      gap: 20,
    }



  }
)

export default ProfileScreen
